document.addEventListener("DOMContentLoaded",function(event){let boxes=document.querySelectorAll(".mod_member_picture_feed_upload .fineuploader-container");if(boxes){boxes.forEach(function(box,index){box.addEventListener("click",event=>{event.stopPropagation();let container=event.target;let parent=container.parentNode;if(parent){let btn=parent.querySelector('input[type="file"][name="fileupload_fineuploader"]');if(btn){btn.click()}}})})}});